from django import template
from django.contrib.staticfiles.storage import staticfiles_storage
from django.templatetags.static import StaticNode

register = template.Library()


def static(path):
    return staticfiles_storage.url(path)


class StaticFilesNode(StaticNode):

    def url(self, context):
        path = self.path.resolve(context)
        return static(path)


@register.tag('templates')
def do_static(parser, token):
    """
    A template tag that returns the URL to a file
    using staticfiles' storage backend

    Usage::

        {% templates path [as varname] %}

    Examples::

        {% templates "myapp/css/base.css" %}
        {% templates variable_with_path %}
        {% templates "myapp/css/base.css" as admin_base_css %}
        {% templates variable_with_path as varname %}
    """
    return StaticFilesNode.handle_token(parser, token)
